import InfoCard from "./components/InfoCard";
import Header from "./components/Header";
import Body from "./components/Body";
import Footer from "./components/Footer";
import { useRef } from "react";


function App() {
  const targetRef1 = useRef(null);
  const targetRef2 = useRef(null);

  const scrollToTarget1 = () => {
    targetRef1.current.scrollIntoView({ behavior: 'smooth' });
  };

  const scrollToTarget2 = () => {
    targetRef2.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div>
      <InfoCard />
      <Header scrollToTarget1={scrollToTarget1} scrollToTarget2={scrollToTarget2} />
      <Body targetRef1={targetRef1} targetRef2={targetRef2} />
      <Footer />
    </div>
  );
}

export default App;
