import { useState, useEffect } from 'react';
import '../styles/Service.css';
import carData from '../carData.json';

export default function Service() {
    const [price, setPrice] = useState('');
    const [service, setService] = useState('');

    const [error, setError] = useState(false);
    const [success, setSuccess] = useState(false);
    const [loading, setLoading] = useState(false);

    const [make, setMake] = useState('');
    const [model, setModel] = useState('');
    const [year, setYear] = useState('');

    const [carDataList, setCarDataList] = useState(carData);


    const user = JSON.parse(localStorage.getItem('user'));

    if(!user) {
        window.location.assign('/login');
    }


    const handleSubmit = async (e) => {
        e.preventDefault();

        const result = {
            make,
            model,
            service,
            price
        }

        console.log(result);

        return;
        setLoading(true);

        if(price == '' || price[0] == ' '){
            setError('Please fill in all fields');

            setTimeout(() => {
                setError(false);
            }, 1800);

            return;
        }

        // const service = {
        //     make: 'Toyota',
        //     model: 'Corrolla',
        //     service: 'Tyre Change',
        //     price
        // }


        const response = await fetch('/api/services', {
            method: 'POST',
            body: JSON.stringify(service),
            headers: {
            'Content-Type': 'application/json'
            }
        });

        const json = response.json();

        if(response.ok){
            setPrice('');
            setSuccess(true);

            setTimeout(() => {
                setSuccess(false);
            }, 1800);
        }

        setLoading(false);
    }

    const uniqueMakes = [...new Set(carDataList.map(car => car.make))].sort();

    useEffect(() => {
        // Initially set model to the first model of the first make
        if (!model && uniqueMakes.length > 0) {
          setModel(uniqueMakes[0].model);
        }
      }, [uniqueMakes, model]);


    const handleChangeMake = (event) => {
        setMake(event.target.value);

        setModel(''); // Reset model when make changes
    };

    const handleChangeModel = (event) => {
        setModel(event.target.value);
    };

    const handleChangeYear = (event) => {
        setYear(event.target.value);
    };



    return(
        <div>
             <h2 className='login_header'>Create Service</h2>
                <div className='login_wrapper'>
                    <form className='login_form' onSubmit={handleSubmit}>
                        {error &&
                        <div className='error_indicator'>
                            <p>{ error }</p>
                        </div>}
                        {success && 
                            <div className='success_indicator'>
                                <p>Appointment created Successfully.</p>
                            </div>
                        }

                        <label className='password_label hide_for_mobile'>Make</label>
                            <select className='hide_for_mobile'>
                                <option></option>
                            </select>
                        <label className='password_label hide_for_mobile'>Model</label>
                        <select className='hide_for_mobile'>
                            <option></option>
                        </select>
                        <label className='password_label hide_for_mobile'>Service</label>
                        <select className='hide_for_mobile'>  
                            <option></option>
                        </select>
                        <label className='price_label hide_for_mobile'>Price</label>
                        <input className='hide_for_mobile' type='text' value={price} onChange={(e) => setPrice(e.target.value)} />
                        <button className='submit_button hide_for_mobile' disabled={loading}>Create Service</button>

                        <label className='mobile_label'>Make</label>
                        <select id="make" value={make} onChange={handleChangeMake} >
                            {
                                uniqueMakes.map((car) => {
                                    return <option key={car}>{ car }</option>
                                })
                            }
                        </select>
                        <label className='mobile_label'>Model</label>
                        <select id="model" value={model} onChange={handleChangeModel}>
                        {make &&
                        carDataList.filter((car) => car.make === make).map((car, index) => (
                            <option key={index} value={car.model}>
                                {car.model}
                            </option>
                        ))}
                        </select>

                        <label className='mobile_label'>Service</label>
                        <input type='text' value={service} onChange={(e) => setService(e.target.value)}/>
                        <label className='mobile_label'>Price</label>
                        <input type='text'className='mobile_label' value={price} onChange={(e) => setPrice(e.target.value)} />
                        <button className='submit_button' disabled={loading}>Create Service</button>
                    </form>
                </div>
        </div>
    )
}
