import React, { useState, useEffect } from 'react';
import carData from '../carData.json';

function ServiceTest() {
  const [data, setData] = useState(carData);
  const [selectedMake, setSelectedMake] = useState('');
  const [availableModels, setAvailableModels] = useState([]);
  const [selectedModel, setSelectedModel] = useState('');

    const uniqueMakes = new Set(data.map(car => car.make));
    setAvailableModels(data.filter(car => car.make === uniqueMakes[0])); // Initially show models for the first make

  const handleMakeChange = (event) => {
    const selectedMake = event.target.value;
    setSelectedMake(selectedMake);

    // Filter models based on selected make
    setAvailableModels(data.filter(car => car.make === selectedMake));
    setSelectedModel(availableModels[0]?.model || ''); // Set default model if available
  };

  const handleModelChange = (event) => {
    setSelectedModel(event.target.value);
  };

  return (
    <form>
      <select value={selectedMake} onChange={handleMakeChange}>
        <option value="">Select Make</option>
        {new Array(uniqueMakes).fill().map((_, index) => (
          <option key={index} value={[...uniqueMakes][index]}>
            {[...uniqueMakes][index]}
          </option>
        ))}
      </select>
      <select value={selectedModel} onChange={handleModelChange}>
        {availableModels.map(model => (
          <option key={model.model} value={model.model}>
            {model.model}
          </option>
        ))}
      </select>
      <input type="text" placeholder="Year" />
    </form>
  );
}

export default ServiceTest;
