import { useState } from 'react';
import '../styles/Appointment.css';

export default function Appointment() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [date, setDate] = useState('');

    const [error, setError] = useState(false);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        setLoading(true);
        const appointment = {
            name,
            email,
            phone,
            make: 'BMW',
            model: 'M4',
            service: 'Oil Change',
            date
        }

        console.log(appointment);
        return false;

        const response = await fetch('/api/appointments', {
            method: 'POST',
            body: JSON.stringify(appointment),
            headers: {
                'Content-Type': 'application/json'
            }
        });

        const json = await response.json();

        if(response.ok){
            setName('');
            setEmail('');
            setPhone('');
            setDate('');
        }

        setLoading(false);
    }

    return(
        <div>
             <h2 className='login_header'>Book an Appointment</h2>
                <div className='login_wrapper'>
                    <form className='login_form' onSubmit={handleSubmit}>
                        {error &&
                        <div className='error_indicator'>
                            <p>{ error }</p>
                        </div>}
                        <label className='email_label'>Name</label>
                        <input type='text' value={name} onChange={(e) => setName(e.target.value)}/>
                        <label className='password_label'>Email</label>
                        <input type='text' value={email} onChange={(e) => setEmail(e.target.value)}/>
                        <label className='phone_label'>Phone Number</label>
                        <input type='text' value={phone} onChange={(e) => setPhone(e.target.value)}/>
                        <label className='password_label'>Make</label>
                        <select>
                            <option></option>
                        </select>
                        <label className='password_label'>Model</label>
                        <select>
                            <option></option>
                        </select>
                        <label className='password_label'>Service</label>
                        <select>
                            <option></option>
                        </select>
                        <label className='password_label'>date</label>
                        <input type='date' value={date} onChange={(e) => setDate(e.target.value)}/>
                        <button className='submit_button' disabled={loading}>Get Free Quote</button>
                    </form>
                </div>
        </div>
    )
}
