import { useState, useEffect } from 'react';
import '../styles/Admin.css';
import { Link } from 'react-router-dom';

export default function Admin() {

    const [appointments, setAppointments] = useState([]);
    const [accounts, setAccounts] = useState([]);
    const [activeTab, setActiveTab] = useState(0);

    const handleTab = (index) => {
        setActiveTab(index);
    }

    const getAppointments = async(e) => {
        const response = await fetch('/api/appointments');
        const json = await response.json();
        setAppointments(json);
    }

    const getAccounts = async(e) => {
        const response = await fetch('/api/users');
        const json = await response.json();
        setAccounts(json);
    }

    useEffect(() => {
        getAppointments();
        getAccounts();
    }, []);

    return(
        <div className='admin'>
            <div className="create_buttons">
                <button><Link to='/create'>Create New Account</Link></button>
                <button><Link to='/service'>Create New Service</Link></button>
            </div>

            <div className="tabs">
                <p onClick={() => handleTab(0)}>Appointments</p>
                <p>Accounts</p>
            </div>

            <table className='submitted_forms table_block'>
                    <thead>
                        <tr>                        
                            <th>Name</th>
                            <th>Service</th>
                            <th>Date</th>
                        </tr>
                    </thead>
                    <tbody>

                        {appointments && appointments.map((appointment) => {
                            return(
                                <tr key={appointment._id}>
                                    <td>{ appointment.name }</td>
                                    <td>{ appointment.service }</td>
                                    <td>{ appointment.date }</td>
                                </tr>
                            )
                        })}

                    </tbody>
                </table>


                    <table className='submitted_forms table_block'>
                    <thead>
                        <tr>                        
                            <th>Name</th>
                            <th>Email</th>
                        </tr>
                    </thead>
                    <tbody>  
                        {accounts && accounts.map((account) => {
                            return(
                                <tr key={account._id}>
                                    <td>{ account.name }</td>
                                    <td>{ account.email }</td>                                
                                </tr>
                            )
                        })}

                    </tbody>
                </table>
        </div>
    )
}
